footer.script:
  $( document ).ready(function() {
  $('#start_date').datetimepicker({
        minDate: 0,  // disable past date
    });
  $('#end_date').datetimepicker({
        minDate: 0,  // disable past date
    });
  $('#date-start').on('change', function(e, date)
    {
    $('#end_date').datetimepicker('setMinDate', date);
    });

    });

 $('.login_btn').on('click', function(){
    $('#RegistrationModal').modal('toggle')
    $('#LoginModal').modal('toggle')
})

$('.register-btn').on('click', function(){
    $('#LoginModal').modal('toggle')
    $('#RegistrationModal').modal('toggle')
  })


$('select#district_id').on('change', function(){
    var val = $(this).val();
$('select#place_id > option').each(function(){
    if($(this).data('distid') == val){
        $(this).show()
    }else{
        $(this).hide()
    }
  $('select#place_id').val('').trigger('change')
})

})
  $('select#place_id').on('change', function(){
    var val = $(this).val();
$('select#venue_id > option').each(function(){
    if($(this).data('place') == val){
        $(this).show()
    }else{
        $(this).hide()
    }
})
  $('select#venue_id').val('').trigger('change')
})


  $('input#start_date').on('change', function(){
  var currentDate = $('input#start_date').val();
  console.log('currentDate.....111111 date change start date');
  console.log(currentDate);
  currentDate = new Date(currentDate);
  console.log(currentDate);
  $("#calendar").fullCalendar( 'gotoDate', currentDate );




  console.log('currentDate.....3333333333');

    })
















































  document.getElementById("venue_id").addEventListener("change",function() {
                var calendarEl = document.getElementById('calendar');
                var currentDate = $('input#start_date').val();
                var venue =  $('select#venue_id').val();
                console.log('currentDate calendar.....');
                console.log(currentDate);
                console.log(new Date());


            if(currentDate == '' || currentDate == null){
                currentDate = new Date();
            }else{
                currentDate = new Date(currentDate);

                }







              var calendar = new FullCalendar.Calendar(calendarEl, {
                eventClick: function(info) {
                  var eventObj = info.event;
                  if (eventObj.start) {
                    alert(
                      'Start Time :-' + eventObj.startStr +  ' : ' + eventObj.extendedProps.startStr + '.\n' +
                      'End Time :-' + eventObj.endStr +  ' : '  + eventObj.extendedProps.endStr
                    );
                    info.jsEvent.preventDefault(); // prevents browser from following link in current tab.
                  }
                },

                initialDate: currentDate,
                events: {
                    url: '/ajax/reservations/get/' + venue,
                    method: 'POST',
                    color: '#5aaaff',
               },
              });

      calendar.render();
    })
